import React from 'react';
import './App.css';

class DataTable extends React.Component
{
    constructor(props)
    {
        super();
        this.state = 
        {
            keyHeaders: props.keyHeaders,//["id","nombre","fecha"],
            nameHeaders: props.nameHeaders,
            dataVisible: [{idVenta:1}],
            dataCount:3,
            pages:0,
            url: props.url,
            inPage:1,
            take: props.take
        }

        this.getDataFromServer();
    }
    render()
    {
        return(
            <div>
                <table className="table table-hover table-sm">
                    <thead className="">
                        <tr>
                            {
                                this.state.nameHeaders.map(n =>
                                    <th key={n}>{n}</th>
                                )
                            }
                        </tr>
                    </thead>
                    <tbody>
                    {this.state.dataVisible.map(datavisible =>
                        <tr key={datavisible.idVenta}>
                        {
                            this.state.keyHeaders.map(column => 
                                <td key={column}>{datavisible[column]}</td>
                            )
                        }
                        </tr>
                    )}
                    </tbody>
                </table>
                <div className="row">
                    <div className="col-6">Viendo {this.state.dataVisible.length} de {this.state.dataCount}</div>
                    <div className="col-6">
                        <button className="m-2 btn btn-secondary btn-sm"><i class="fas fa-chevron-left"></i> Anterior</button>
                        <button className="m-2 btn btn-secondary btn-sm">Siguiente <i class="fas fa-chevron-right"></i></button>
                    </div>
                </div>
            </div>
        )
    }
    getDataFromServer()
    {
        fetch(`${this.state.url}?skip=${this.state.inPage*this.state.take}&take=${this.state.take}`)
        .then(data => data.json())
        .then(data => this.props.callBackData(data))
        .then(data => this.setState({dataVisible: data}))
    }
}

export default DataTable;
